mapper.insert:
/* userDelRegist */
INSERT INTO APPLICATION(
	IN_KEY
	,MEM_ID
	,TYPE
	,REC_NM
	,REC_PHONE
	,REC_TARGET
	,REC_ADDR
	,REC_HOU
	,REC_TXT
	,GOO_COORDINATE
	,CRE_DAY
	,ARR_DAY
	,CHK
	,PASSWORD
) VALUES (
	#{inKey}
	,#{memId}
	,#{type}
	,#{recNm}
	,#{recPhone}
	,#{recTarget}
	,#{recAddr}
	,#{recHou}
	,#{recTxt}
	,#{gooCoordinate}
	,#{creDay}
	,#{arrDay}
	,'N'
	,#{password}
);

mapper.select:
/* outDayList */
SELECT 
	OUT_KEY AS outKey
	,OUT_DAY AS outDay
	,OUT_TXT AS outTxt
FROM OUT_DAY;

mapper.update:
/* adminDelUpdate */
UPDATE APPLICATION
SET		
	REC_NM = #{recNm}
	,REC_PHONE = #{recPhone} 
	,REC_TARGET = #{recTarget}
	,REC_ADDR = #{recAddr}
	,REC_HOU = #{recHou}						
	,CRE_DAY = #{creDay}
	,ARR_DAY = #{arrDay}
	,CHK = 'Y'
	,PASSWORD = #{password}
WHERE 
	IN_KEY = #{inKey};

mapper.select:
/* cntInKey */
SELECT COUNT(*) AS cnt
FROM APPLICATION
WHERE SUBSTR(IN_KEY,3,8) = #{inKeyDay};

/* lastInKey */
SELECT IN_KEY AS inKey
FROM APPLICATION
WHERE SUBSTR(IN_KEY,3,8) = #{inKeyDay}
AND SUBSTR(IN_KEY, 11) = (
	SELECT MAX(cast(SUBSTR(IN_KEY,11) AS INTEGER)) 
	FROM APPLICATION
	WHERE SUBSTR(IN_KEY,3,8) = #{inKeyDay}
);

/* cntTotalPacketInfo */
SELECT IFNULL(MAX(SN),0) AS sn
FROM SJ_NUM
WHERE OUT_KEY = #{outKey};

mapper.insert:
/* packetInfoRegist */
INSERT INTO SJ_NUM(
	SJ_KEY
	,IN_KEY
	,SN
	,OUT_KEY
	,WIDTH
	,LENGTH
	,HEIGHT
	,WEIGHT
	,COST
)
VALUES(
	#{sjKey}
	,#{inKey}
	,#{sn}
	,#{outKey}
	,#{width}
	,#{length}
	,#{height}
	,#{weight}
	,#{cost}
);

mapper.delete:
/* delPacketInfo */
UPDATE SJ_NUM
SET IN_KEY = #{outKey}
WHERE IN_KEY = #{inKey};
